if doc.parent_supplier:
    if doc.supplier_primary_address or doc.country:
        city=frappe.db.get_value('Address',{'name':doc.supplier_primary_address},'city')
        if not frappe.get_list("Child Supplier Details",{'supplier_name':doc.name,'parent':doc.parent_supplier}):
            supplier_doc = frappe.get_doc('Supplier',doc.parent_supplier)
            supplier_doc = supplier_doc.append('individual_supplier_detail', {
                'supplier_name':doc.name,
                'country':doc.country,
                'city':city
            })
            supplier_doc.save()
        else:
            doc_name = frappe.db.get_value('Child Supplier Details',{'supplier_name':doc.name},'name')
            isd_doc = frappe.get_doc('Child Supplier Details',doc_name)
            isd_doc.update({
                'supplier_name':doc.name,
                'country':doc.country,
                'city':city
            })
            isd_doc.save()

if doc.is_manager==0 and doc.report_manager!=None:
    city=frappe.db.get_value('Address',{'name':doc.supplier_primary_address},'city')
    if not frappe.get_list("Report Member Details",{'supplier_name':doc.name,'parent':doc.report_manager}):
        supplier_doc = frappe.get_doc('Supplier',doc.report_manager)
        supplier_doc = supplier_doc.append('report_member_details', {
            'supplier_name':doc.name,
            'country':doc.country,
            'city':city
        })
        supplier_doc.save()
    else:
        doc_name = frappe.db.get_value('Report Member Details',{'supplier_name':doc.name},'name')
        rmd_doc = frappe.get_doc('Report Member Details',doc_name)
        rmd_doc.update({
            'supplier_name':doc.name,
            'country':doc.country,
            'city':city
        })
        rmd_doc.save()

if doc.manager_id==None:
    doc.manager_id=doc.name
    doc.save()

if doc.supplier_items:
    for row in doc.supplier_items:
        id=frappe.db.get_value('Supplier',{'name':row.supplier},'product_lookup')
        if id =="Template and Variant ID" and ((row.template_id==None or row.template_id=='') and (row.variant_id==None or row.variant_id=='')):
            frappe.throw(_('Template ID and Variant ID is mandatory for supplier '+row.supplier+'.'))
        elif id =="Template and Variant ID" and (row.template_id==None or row.template_id==''):
            frappe.throw(_('Template ID is mandatory for supplier '+row.supplier+'.'))
        elif id =="Template and Variant ID" and (row.variant_id==None or row.variant_id==''):
            frappe.throw(_('Variant ID is mandatory for supplier '+row.supplier+'.'))
        elif id=="Unique ID" and (row.unique_id==None or row.unique_id==''):
            frappe.throw(_('Unique ID is mandatory for supplier '+row.supplier+'.'))
