if doc.parent_supplier:
    if doc.supplier_primary_address or doc.country:
        city=frappe.db.get_value('Address',{'name':doc.supplier_primary_address},'city')
        if not frappe.get_list("Individual Supplier Details",{'supplier_name':doc.name}):
            supplier_doc = frappe.get_doc('Supplier',doc.parent_supplier)
            supplier_doc = supplier_doc.append('individual_supplier_details', {
                'supplier_name':doc.name,
                'country':doc.country,
                'city':city
            })
            supplier_doc.save()
        else:
            doc_name = frappe.db.get_value('Individual Supplier Details',{'supplier_name':doc.name},'name')
            isd_doc = frappe.get_doc('Individual Supplier Details',doc_name)
            isd_doc.update({
                'supplier_name':doc.name,
                'country':doc.country,
                'city':city
            })
            isd_doc.save()
